    private void jTextCedulaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextCedulaKeyTyped
        // TODO add your handling code here:
        char texto = evt.getKeyChar();
        if (((texto < '0')
                || (texto > '9'))
                && (texto != evt.VK_BACK_SPACE)) {
            evt.consume();
        }
        String datos = jTextCedula.getText();
        if (datos.length() >= 9) {
            evt.consume();
        }
    }//GEN-LAST:event_jTextCedulaKeyTyped

